var distance = Number(msg.payload) * 0.7 * Math.PI
var percent = Math.round(distance * 100 / flow.get('distance'))

var msg2 = null

if (percent >= 100) {
    percent = 100
    if (flow.get("running")) {
        flow.set("running", false)
        msg2 = {
            "winner": 1
        }
    }
}

var msg1 = {
    "topic": "heiafr/ms/top/gauge",
    "payload": JSON.stringify(
        {
            "value" : percent
        }
    )
}


return [msg1, msg2]

 // time in miliseconds since last turn, valid for a 27 inches wheel
var time=Number(msg.payload)
var speed = 7756 / time
var power = 0
var coeff=flow.get('coeffs')
for (var i = 1; i < 4; i++) {
    power += coeff[i - 1] * speed ** i
}
var energy = power * time / 1000.

if (energy<0) {
    energy=0;
}
if (power<0) {
    power=0;
}
// speed in km/h, power in Watts, energy in Joules
msg.payload=[energy,speed,power]
return msg;

const empty = 935000
const full = 3578000
const v = Number(msg.payload)

var res = Math.round((v - empty) * 100 / (full - empty))
if (res > 100) res = 100
if (res < 0)   res = 0

msg.payload = res
return msg;
